GaugeText.GaugeString:
  					%((A:GPS Position Lat,degrees) 0 <)%{if}S%{else}N%{end}
  					%((A:GPS Position Lat,degrees) abs flr)%!2d!
  					.%((A:GPS Position Lat,degrees) abs d flr - 60 *)%!4.2f!
  					%((A:GPS Position Lon,degrees) 0 <)%{if} W%{else} E%{end}
					%((A:GPS Position Lon,degrees) abs flr)%!3d!
					.%((A:GPS Position Lon,degrees) abs d flr - 60 *)%!4.2f!

MouseClick.Script:
						(M:Event) 'LeftSingle' scmp 0 == 
						if{ 
							2 (>L:APU Start Pressed,number) 
                    		(A:APU PCT RPM,percent) 
                    		1 > 
                    		if{ 1 (>K:APU_OFF_SWITCH) } 
                   			els{ 1 (>K:APU_STARTER) } 
						} 
						(M:Event) 'LeftRelease' scmp 0 == if{ 0 (>L:APU Start Pressed,number) }

